<?php

namespace App\Repository;

/**
 * RecaudacionAnualRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class RecaudacionAnualRepository extends \Doctrine\ORM\EntityRepository
{
    public function findAll()
    {
        return $this->findBy(array(), array('anno' => 'ASC'));
    }
}
